const cheerio = require('cheerio');

function getEpisodeDataForSeason(htmlString, seasonNumber) {
  const $ = cheerio.load(htmlString);

  // Find the div containing the specified season number (improved selector)
  const seasonDiv = $('div').filter(function() {
    const text = $(this).text().trim();
    // Check for both "Season" and the season number to ensure accuracy
    return text.startsWith(`Season${seasonNumber}`);
  });

  // Check if the div is found
  if (!seasonDiv.length) {
    return null; // No season div found, return null
  }

  // Extract episode data from child anchor tags (a elements)
  const episodeData = seasonDiv.find('a').map(function() {
    const episodeNumber = $(this).text().trim().split('.')[0]; // Extract episode number
    const episodeUrl = $(this).attr('href'); // Get episode URL

    return { episodeNumber, episodeUrl };
  }).get();

  return episodeData;
}

function findSeasonDiv(htmlString) {
  const $ = cheerio.load(htmlString);

  // Combine class selector with text filter using contains()
  const season2Div = $('div.alert.alert-info-ex.col-sm-12').filter(function() {
    const text = $(this).text().trim();
    return text.startsWith('Season2');
  });

  return season2Div; // Return the Cheerio object representing the matching div
}

// Example usage
const htmlString = html; // Replace with your actual HTML content

// Find the Season 2 div
const season2Div = findSeasonDiv(htmlString);


if (season2Div.length) {

  // Extract episode data for Season 2
  const episodeData = getEpisodeDataForSeason(htmlString, 2); // Season 2 is assumed to be at index 2
  console.log(episodeData)

  if (episodeData) {
    console.log("Episode data for Season 2:");
    episodeData.forEach(episode => {
      console.log(`  Episode ${episode.episodeNumber}: ${episode.episodeUrl}`);
    });
  } else {
    console.log("No episode data found for Season 2.");
  }
} else {
  console.log("Season 2 div not found.");
}
